package com.example.teleexpertise.controller.auth;

import com.example.teleexpertise.model.utilisateur;
import com.example.teleexpertise.service.AuthService;
import jakarta.servlet.ServletException;
import jakarta.servlet.annotation.WebServlet;
import jakarta.servlet.http.HttpServlet;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;
import jakarta.servlet.http.HttpSession;

import java.io.IOException;

@WebServlet("/login")
public class LoginServlet extends HttpServlet {

    private AuthService authService;

    @Override
    public void init() throws ServletException {
        super.init();
        this.authService = new AuthService();
        System.out.println("✅ LoginServlet initialisé avec succès");
    }

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        request.getRequestDispatcher("/index.jsp").forward(request, response);
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {

        request.setCharacterEncoding("UTF-8");
        response.setContentType("text/html;charset=UTF-8");

        String username = request.getParameter("username");
        String password = request.getParameter("password");

        try {
            utilisateur user = authService.authenticate(username, password);

            // Create session
            HttpSession session = request.getSession();
            session.setAttribute("user", user);
            session.setAttribute("userId", user.getId());
            session.setAttribute("userRole", user.getRole());
            session.setAttribute("userName", user.getPrenom() + " " + user.getNom());
            session.setMaxInactiveInterval(30 * 60);

            String redirectUrl = authService.getRedirectUrlByRole(user.getRole());
            response.sendRedirect(request.getContextPath() + redirectUrl);

        } catch (Exception e) {
            // Handle error
            request.setAttribute("error", e.getMessage());
            request.setAttribute("username", username);
            request.getRequestDispatcher("/index.jsp").forward(request, response);
        }
    }
}

package com.example.teleexpertise.controller.auth;

import com.example.teleexpertise.model.utilisateur;
import com.example.teleexpertise.service.AuthService;
import com.example.teleexpertise.util.CsrfUtil;
import jakarta.servlet.ServletException;
import jakarta.servlet.annotation.WebServlet;
import jakarta.servlet.http.HttpServlet;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;
import jakarta.servlet.http.HttpSession;

import java.io.IOException;

@WebServlet("/login")
public class LoginServlet extends HttpServlet {

    private AuthService authService;

    @Override
    public void init() throws ServletException {
        super.init();
        this.authService = new AuthService();
        System.out.println("✅ LoginServlet initialisé avec succès");
    }

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        // Générer un token CSRF pour le formulaire
        HttpSession session = request.getSession(true);
        String csrfToken = CsrfUtil.generateToken(session);
        request.setAttribute("csrfToken", csrfToken);
        
        request.getRequestDispatcher("/index.jsp").forward(request, response);
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {

        request.setCharacterEncoding("UTF-8");
        response.setContentType("text/html;charset=UTF-8");

        // ✅ Validation du token CSRF
        if (!CsrfUtil.validateToken(request)) {
            request.setAttribute("error", "Token de sécurité invalide. Veuillez réessayer.");
            request.setAttribute("username", request.getParameter("username"));
            
            // Régénérer un nouveau token
            HttpSession session = request.getSession(true);
            String csrfToken = CsrfUtil.generateToken(session);
            request.setAttribute("csrfToken", csrfToken);
            
            request.getRequestDispatcher("/index.jsp").forward(request, response);
            return;
        }

        String username = request.getParameter("username");
        String password = request.getParameter("password");

        try {
            utilisateur user = authService.authenticate(username, password);

            // Create session
            HttpSession session = request.getSession();
            session.setAttribute("user", user);
            session.setAttribute("userId", user.getId());
            session.setAttribute("userRole", user.getRole());
            session.setAttribute("userName", user.getPrenom() + " " + user.getNom());
            session.setMaxInactiveInterval(30 * 60);
            
            // Régénérer un nouveau token CSRF après connexion réussie
            CsrfUtil.generateToken(session);

            String redirectUrl = authService.getRedirectUrlByRole(user.getRole());
            response.sendRedirect(request.getContextPath() + redirectUrl);

        } catch (Exception e) {
            // Handle error
            request.setAttribute("error", e.getMessage());
            request.setAttribute("username", username);
            
            // Régénérer un nouveau token en cas d'erreur
            HttpSession session = request.getSession(true);
            String csrfToken = CsrfUtil.generateToken(session);
            request.setAttribute("csrfToken", csrfToken);
            
            request.getRequestDispatcher("/index.jsp").forward(request, response);
        }
    }
}

package com.example.teleexpertise.controller.auth;

import com.example.teleexpertise.service.AuthService;
import jakarta.servlet.ServletException;
import jakarta.servlet.annotation.WebServlet;
import jakarta.servlet.http.HttpServlet;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;

import java.io.IOException;

@WebServlet(value = "/register")
public class registerServlet extends HttpServlet {
    private AuthService authService;

    @Override
    public void init() throws ServletException {
        super.init();
        this.authService = new AuthService();
    }

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        response.setContentType("text/html;charset=UTF-8");
        request.getRequestDispatcher("/WEB-INF/jsp/register.jsp").forward(request, response);
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        response.setContentType("text/html;charset=UTF-8");
        request.setCharacterEncoding("UTF-8");

        // Récupération des paramètres du formulaire
        String nom = request.getParameter("lastName");
        String prenom = request.getParameter("firstName");
        String email = request.getParameter("email");
        String motDePasse = request.getParameter("password");
        String confirmPassword = request.getParameter("confirmPassword");
        String role = request.getParameter("role");
        String specialite = request.getParameter("specialite");

        try {
            authService.registerUser(nom, prenom, email, motDePasse, confirmPassword, role, specialite);
            request.getSession().setAttribute("successMessage", "Inscription réussie ! Vous pouvez maintenant vous connecter.");
            response.sendRedirect(request.getContextPath() + "/login");

        } catch (Exception e) {
            // Error - forward back to form with error message and preserved data
            request.setAttribute("error", e.getMessage());
            request.setAttribute("nom", nom);
            request.setAttribute("prenom", prenom);
            request.setAttribute("email", email);
            request.setAttribute("role", role);
            request.setAttribute("specialite", specialite);
            request.getRequestDispatcher("/WEB-INF/jsp/register.jsp").forward(request, response);
        }
    }
}

package com.example.teleexpertise.controller.auth;

import com.example.teleexpertise.service.AuthService;
import com.example.teleexpertise.util.CsrfUtil;
import jakarta.servlet.ServletException;
import jakarta.servlet.annotation.WebServlet;
import jakarta.servlet.http.HttpServlet;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;
import jakarta.servlet.http.HttpSession;

import java.io.IOException;

@WebServlet(value = "/register")
public class registerServlet extends HttpServlet {
    private AuthService authService;

    @Override
    public void init() throws ServletException {
        super.init();
        this.authService = new AuthService();
    }

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        response.setContentType("text/html;charset=UTF-8");

        // Générer un token CSRF pour le formulaire
        HttpSession session = request.getSession(true);
        String csrfToken = CsrfUtil.generateToken(session);
        request.setAttribute("csrfToken", csrfToken);

        request.getRequestDispatcher("/WEB-INF/jsp/register.jsp").forward(request, response);
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        response.setContentType("text/html;charset=UTF-8");
        request.setCharacterEncoding("UTF-8");

        // ✅ Validation du token CSRF
        if (!CsrfUtil.validateToken(request)) {
            request.setAttribute("error", "Token de sécurité invalide. Veuillez réessayer.");

            // Préserver les données saisies
            request.setAttribute("nom", request.getParameter("lastName"));
            request.setAttribute("prenom", request.getParameter("firstName"));
            request.setAttribute("email", request.getParameter("email"));
            request.setAttribute("role", request.getParameter("role"));
            request.setAttribute("specialite", request.getParameter("specialite"));

            // Régénérer un nouveau token
            HttpSession session = request.getSession(true);
            String csrfToken = CsrfUtil.generateToken(session);
            request.setAttribute("csrfToken", csrfToken);

            request.getRequestDispatcher("/WEB-INF/jsp/register.jsp").forward(request, response);
            return;
        }

        // Récupération des paramètres du formulaire
        String nom = request.getParameter("lastName");
        String prenom = request.getParameter("firstName");
        String email = request.getParameter("email");
        String motDePasse = request.getParameter("password");
        String confirmPassword = request.getParameter("confirmPassword");
        String role = request.getParameter("role");
        String specialite = request.getParameter("specialite");

        try {
            authService.registerUser(nom, prenom, email, motDePasse, confirmPassword, role, specialite);
            request.getSession().setAttribute("successMessage", "Inscription réussie ! Vous pouvez maintenant vous connecter.");
            response.sendRedirect(request.getContextPath() + "/login");

        } catch (Exception e) {
            // Error - forward back to form with error message and preserved data
            request.setAttribute("error", e.getMessage());
            request.setAttribute("nom", nom);
            request.setAttribute("prenom", prenom);
            request.setAttribute("email", email);
            request.setAttribute("role", role);
            request.setAttribute("specialite", specialite);

            // Régénérer un nouveau token en cas d'erreur
            HttpSession session = request.getSession(true);
            String csrfToken = CsrfUtil.generateToken(session);
            request.setAttribute("csrfToken", csrfToken);

            request.getRequestDispatcher("/WEB-INF/jsp/register.jsp").forward(request, response);
        }
    }
}

package com.example.teleexpertise.dao;

import com.example.teleexpertise.model.Infirmier;
import com.example.teleexpertise.util.JpaUtil;
import jakarta.persistence.EntityManager;
import jakarta.persistence.TypedQuery;

import java.util.List;

/**
 * DAO pour Infirmier - Hérite de GenericDAOImpl
 */
public class InfirmierDAO extends GenericDAOImpl<Infirmier> {

    public InfirmierDAO() {
        super(Infirmier.class);
    }

    // ✅ save(), update(), findById(), findAll(), deleteById() sont HÉRITÉS
    // Pas besoin de les réécrire !

    /**
     * Trouver un infirmier par email
     */
    public Infirmier findByEmail(String email) {
        EntityManager em = JpaUtil.getEntityManager();
        try {
            TypedQuery<Infirmier> query = em.createQuery(
                "SELECT i FROM Infirmier i WHERE i.email = :email",
                Infirmier.class
            );
            query.setParameter("email", email);
            List<Infirmier> results = query.getResultList();
            return results.isEmpty() ? null : results.get(0);
        } finally {
            em.close();
        }
    }
}

